var express = require("express");
var app = express();

//body-parser is required to be able to 
// get data from the body of a form
var bp = require("body-parser");
app.use(express.static("public"));

//IMPORTANT
app.use(bp.urlencoded({extended:true}));

var campgrounds = [
    {name: "Salmon Creek",
    image: "http://haulihuvila.com/wp-content/uploads/2012/09/hauli-huvila-campgrounds-lg.jpg"    
    },

    {name: "Boulder Valley",
    image: "data:image/jpeg;base64,"    
    },

    {name: "Yeti Trail",
    image: "data:image/jpeg;base64,"    
    },
    

];

app.get("/",function(req,res){
    res.render("home.ejs");
});


app.get("/campgrounds",function(req,res){
    res.render("campgrounds.ejs", {cg:campgrounds})
});

app.get("/campgrounds/new",function(req,res){
    res.render("newCampGround.ejs");
});

app.post("/campgrounds",function(req,res){
    // Get data from form, add to campgrounds array, refresh
    var newCG = {
        name: req.body.newCG,
        image: req.body.img_url
    }
    campgrounds.push(newCG);
    res.redirect("/campgrounds");
})

app.listen(3000,function(){
    console.log("server started");
});